{"changed":true,"filter":false,"title":"model_bbs.php","tooltip":"/htdocs/php23/model_bbs.php","value":"<?php\n// DB接続\n\nfunction get_db_connect(){\n    \n     // コネクション取得\n    if (!$link = mysqli_connect(DB_HOST, DB_USER, DB_PASSWD, DB_NAME)) {\n        die('error: ' . mysqli_connect_error());\n    }\n    \n    // 文字コードセット\n    mysqli_set_charset($link, DB_CHARACTER_SET);\n \n    return $link;\n}\n\n// 書き込みを読み込む\nfunction get_bbs($link){\n    \n    // sql生成\n    $sql = 'SELECT * FROM bbs_table';\n    \n    // sqlを実行するget_as_array関数へ引き渡し\n    return get_as_array($link, $sql);\n}\n\n\n// クエリを実行しその結果を取得する関数\nfunction get_as_array($link, $sql) {\n    \n    // 返却配列\n    $data = [];\n    \n    // クエリを実行する\n    if ($result = mysqli_query($link, $sql)){\n        \n        if(mysqli_num_rows($result) > 0){\n            \n            while($row = mysqli_fetch_array($result)){\n                $data[] = $row;\n            }\n        }\n        \n        // 結果セットを開放\n        mysqli_free_result($result);\n    }\n    return $data;\n}\n\n// 入力文字チェック関数\nfunction post_bbs(){\n    \n     if ($_SERVER['REQUEST_METHOD'] === 'POST'){\n         \n         $name = '';\n         $comment = '';\n         \n         if(isset($_POST['name'])){\n            if(mb_strlen($_POST['name']) <= 20 && mb_strlen($_POST['name']) != 0){\n                $name = $_POST['name'];\n            }else if(mb_strlen($_POST['name']) > 20){\n                $error_name = \"!!名前は文字数を20文字以下にしてください!!\".\"\\n\";\n                print $error_name;\n            }else if(mb_strlen($_POST['name']) === 0){\n                $error_noname =\"!!名前を入力してください!!\".\"\\n\";\n                print $error_noname;\n            }\n        }\n        \n        if(isset($_POST['hitokoto'])){\n            if(mb_strlen($_POST['hitokoto']) <= 100 && mb_strlen($_POST['hitokoto']) != 0){\n                $comment = $_POST['hitokoto'];\n            }else if(mb_strlen($_POST['hitokoto']) > 100 ){\n                $error_comment = \"!!コメントは文字数を100文字以下にしてください!!\".\"\\n\";\n                print $error_comment;\n            }else if(mb_strlen($_POST['hitokoto']) === 0){\n                $error_nocomment = \"!!コメントを入力してください!!\".\"\\n\";\n                print $error_nocomment;\n            }\n        }\n        \n        if($name !== '' && $comment !== ''){\n            return update_bbs($name,$comment);\n        } \n        \n    }\n    \n    \n}\n\n// クエリを実行して書き込みをdbに追加する関数\nfunction update_bbs($name,$comment){\n    global $link;\n    if($comment && $name){\n            $date = date('y/m/d H:i:s');\n            \n        \n            $query = \"INSERT INTO bbs_table(name,comment,time) VALUES('$name', '$comment','$date')\";\n      \n            if (mysqli_query($link, $query) === TRUE){\n            print '成功';\n          } else {\n            print '失敗';\n          }\n    }\n}\n// DB切断\n\nfunction close_db_connect($link){\n    // 接続を閉じる\n    mysqli_close($link);\n}","undoManager":{"mark":92,"position":100,"stack":[[{"start":{"row":85,"column":12},"end":{"row":85,"column":13},"action":"insert","lines":["D"],"id":305}],[{"start":{"row":85,"column":12},"end":{"row":85,"column":13},"action":"remove","lines":["D"],"id":306}],[{"start":{"row":85,"column":12},"end":{"row":85,"column":13},"action":"insert","lines":["$"],"id":307},{"start":{"row":85,"column":13},"end":{"row":85,"column":14},"action":"insert","lines":["d"]},{"start":{"row":85,"column":14},"end":{"row":85,"column":15},"action":"insert","lines":["a"]},{"start":{"row":85,"column":15},"end":{"row":85,"column":16},"action":"insert","lines":["t"]},{"start":{"row":85,"column":16},"end":{"row":85,"column":17},"action":"insert","lines":["e"]}],[{"start":{"row":85,"column":17},"end":{"row":85,"column":18},"action":"insert","lines":[" "],"id":308},{"start":{"row":85,"column":18},"end":{"row":85,"column":19},"action":"insert","lines":["="]}],[{"start":{"row":85,"column":19},"end":{"row":85,"column":20},"action":"insert","lines":[" "],"id":309}],[{"start":{